package JavaBasics_1;

public class LoopConcept4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=1;
		int j=i++;
		//post increment
		System.out.println(i);
		System.out.println(j);
		
		//pre increment
		int a=1;
		int b=++a;
		System.out.println(a);
		System.out.println(b);
		
		//post decrement
		
		
		
	}

}
