package JavaBasics_1;

public class StringConcatenation2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//+ is concatenation operator
		
		int a=20;
		int b=30;
		String d ="Hello";
		String e ="World";
		System.out.println(a+b);
		
		System.out.println(d+" "+e);
		
		System.out.println(d+e+a+b);
		
		System.out.println(d+e+(a+b));
		
		System.out.println(a+b+d+e+a+d+b+e);
		
		System.out.println(a+b+d+e+a+b);
		System.out.println("  xxx xx xx");
		System.out.println("The value of a:"+a);
		System.out.println("The value of b:"+b);
		System.out.println("The addition of a and b:"+(a+b));
		System.out.print("Hello");
		System.out.println(" In Selenium");
	
	}

} 
